# -*- coding: utf-8 -*-

import csv
import requests
import time
from bs4 import BeautifulSoup
import os
import re

# Ask which vector map should be generated
print ('Which plasmid maps you want to generate? \n')
print ('[1] cmyc-NL-insert donor')
print ('[2] insert-NL-cmyc donor')
print ('[3] PA-mCitrine-insert acceptor')
print ('[4] insert-mCitrine-PA acceptor')
print ('[5] pDONR223')
print ('[6] pDONR221')
print ('[7] pDONR201')
plasmidtype = input("Choose plasmid (enter numbers 1-7): ")

# Date of today
today = time.strftime("%d/%m/%Y")

# Specify file name
script_dir = os.path.dirname(__file__)
rel_path = "input.csv"
CCSB_csv = os.path.join(script_dir, rel_path)

with open(CCSB_csv, 'rb') as csvfile:
    spamreader = csv.reader(csvfile, delimiter=';', quotechar='|')
    for row in spamreader:
        
        # Generate seq of nterm and cterm of desired plasmid
        if plasmidtype == 1:
            nterm = ""
            templatefile = os.path.join(script_dir, "template-cmyc-NL-GW.gb")
            mapprefix = "pcDNA3.1 myc-NL-GW.gb"
        elif plasmidtype == 2:
            nterm = ""
            templatefile = os.path.join(script_dir, "template-GW-NL-cmyc.gb")
            mapprefix = "pcDNA3.1 GW-NL-myc.gb"
        elif plasmidtype == 3:
            nterm = ""
            templatefile = os.path.join(script_dir, "template-PA-mCitrine-GW.gb")
            mapprefix = "pcDNA3.1 PA-mCit-GW.gb"
        elif plasmidtype == 4:
            nterm = ""
            templatefile = os.path.join(script_dir, "template-GW-mCitrine-PA.gb")
            mapprefix = "pcDNA3.1 GW-mCit-PA.gb"    
        elif plasmidtype == 5:
            nterm =""
            templatefile = os.path.join(script_dir, "template-pDONR223.gb")
            mapprefix = "pDONR223 GW.gb"
        elif plasmidtype == 6:
            nterm = ""
            templatefile = os.path.join(script_dir, "template-pDONR221.gb")
            mapprefix = "pDONR221 GW.gb"
        elif plasmidtype == 7:
            nterm = ""
            templatefile = os.path.join(script_dir, "template-pDONR201.gb")
            mapprefix = "pDONR201 GW.gb"
        
        # Get sequence data of CCSB clone from humanORFeome 7.1
        ccsbID = row[0].strip('CCSB_')
        url = 'http://horfdb.dfci.harvard.edu/hv7/index.php?page=getresults&by=detail&qury=' + ccsbID
        response = requests.get(url)
        htmldata = response.text
        soup = BeautifulSoup(htmldata, features="html.parser")          
        try:
            orf = soup.findAll('font', {"class": "seq"})[0].text
            #symbol = soup.findAll('table', {"class": "dtdata"})[0].text.strip('Symbol: ').strip()
            symbol = soup.findAll('table', {"class": "dtdata"})[0]
            symbol = re.search('Symbol:(.*)Description', symbol.text).group(1).strip()
            #symbol = soup.findAll('td', {"colspan": "2"})
            #for r in symbol:
            #    print r
        except IndexError:
            pass
        
        #remove stop-codon
        orf = orf[:-3]
        seq = nterm + orf
        seq_spaced = ' '.join(seq[i:i+10] for i in range(0, len(seq), 10))
        result = [seq_spaced[i:i+66] for i in range(0, len(seq_spaced), 66)]
        
        # Create row numbering (max. vector map size 15000)
        rownumbering = list(range(1,15000,60))
        
        # Create new file
        filename = mapprefix.replace('GW', symbol + "_CCSB_" + ccsbID)
        #filename = "CCSB_" + ccsbID + " " + mapprefix + ".gb"
        newfile = os.path.join(script_dir, filename)
        fnew = open(newfile, "w+")    
        
        # Read contents of template file and modify
        insert_start = str(len(seq)-len(orf)+1)
        insert_end = str(len(seq))
        
        f = open(templatefile, "r")
        filedata = f.read()
        filedata = filedata.replace('title', filename)
        filedata = filedata.replace('size', str(len(seq))) 
        filedata = filedata.replace('date', today)
        filedata = filedata.replace('insert_name', symbol + "_(CCSB_" + ccsbID + ")")
        filedata = filedata.replace('insert_start', insert_start)
        filedata = filedata.replace('insert_end', insert_end)   
        f.close()
        
        # Write modified template to new file
        fnew.write(filedata)
              
        # Create formatted seq with rownumbering and bases           
        i = 0
        combined_seq = list()
        while i < len(result):
            combined_seq.append(str(rownumbering[i]) + ' ' + result[i] + '\n')
            i += 1
        
        # Append formatted seq to new file and close
        fnew.seek(0,2)
        fnew.writelines(combined_seq)
        fnew.write('//')
        fnew.close()
