# -*- coding: utf-8 -*-

import csv
import requests
import time
from bs4 import BeautifulSoup
import os
import re

# Ask which vector map should be generated
print ('Which plasmid maps you want to generate? \n')
print ('Entries:')
print ('[1] pDONR223')
print ('[2] pDONR221')
print ('[3] pDONR201\n')
print ('LuTHy expression plasmids:')
print ('[4] pcDNA3.1-NL-N1')
print ('[5] pcDNA3.1-NL-C1')
print ('[6] pcDNA3.1-PA-mCit-N2')
print ('[7] pcDNA3.1-PA-mCit-C2')
print ('[8] pcDNA3.1-mCit-N2')
print ('[9] pcDNA3.1-mCit-C2\n')
print ('N2H expression plasmids:')
print ('[10] pYN2H-LEU-N1')
print ('[11] pYN2H-LEU-C1')
print ('[12] pYN2H-TRP-N2')
print ('[13] pYN2H-TRP-C2\n')

plasmidtype = input("Choose backbone (enter numbers 1-13): ")

print ('[1] human ORFeome V8.1')
print ('[2] human ORFeom V7.1')
bank = input("Select human ORFeome version: ")

internal_no = ""
#internal_no = input("Enter internal plasmid number (press enter for none): ")

# Date of today
today = time.strftime("%d/%m/%Y")

# Specify file name
script_dir = os.path.dirname(__file__)
rel_path = "input.csv"
CCSB_csv = os.path.join(script_dir, rel_path)

with open(CCSB_csv, 'rb') as csvfile:
    spamreader = csv.reader(csvfile, delimiter=';', quotechar='|')
    for row in spamreader:
        
        # Generate seq of nterm and cterm of desired plasmid
        if plasmidtype == 4:
            nterm = ""
            templatefile = os.path.join(script_dir, "template-cmyc-NL-GW.gb")
            mapprefix = internal_no + "pcDNA3.1 myc-NL-GW.gb"
        elif plasmidtype == 5:
            nterm = ""
            templatefile = os.path.join(script_dir, "template-GW-NL-cmyc.gb")
            mapprefix = internal_no + "pcDNA3.1 GW-NL-myc.gb"
        elif plasmidtype == 6:
            nterm = ""
            templatefile = os.path.join(script_dir, "template-PA-mCitrine-GW.gb")
            mapprefix = internal_no + "pcDNA3.1 PA-mCit-GW.gb"
        elif plasmidtype == 7:
            nterm = ""
            templatefile = os.path.join(script_dir, "template-GW-mCitrine-PA.gb")
            mapprefix = internal_no + "pcDNA3.1 GW-mCit-PA.gb"    
        elif plasmidtype == 8:
            nterm = ""
            templatefile = os.path.join(script_dir, "template-mCitrine-GW.gb")
            mapprefix = internal_no + "pcDNA3.1 mCit-GW.gb"
        elif plasmidtype == 9:
            nterm = ""
            templatefile = os.path.join(script_dir, "template-GW-mCitrine.gb")
            mapprefix = internal_no + "pcDNA3.1 GW-mCit.gb"
        elif plasmidtype == 1:
            nterm =""
            templatefile = os.path.join(script_dir, "template-pDONR223.gb")
            mapprefix = internal_no + "pDONR223 GW.gb"
        elif plasmidtype == 2:
            nterm = ""
            templatefile = os.path.join(script_dir, "template-pDONR221.gb")
            mapprefix = internal_no + "pDONR221 GW.gb"
        elif plasmidtype == 3:
            nterm = ""
            templatefile = os.path.join(script_dir, "template-pDONR201.gb")
            mapprefix = internal_no + "pDONR201 GW.gb"
        elif plasmidtype == 10:
            nterm = ""
            templatefile = os.path.join(script_dir, "template-pYN2H-LEU-N1.gb")
            mapprefix = internal_no + "pYN2H-LEU-N1 GW.gb"
        elif plasmidtype == 11:
            nterm = ""
            templatefile = os.path.join(script_dir, "template-pYN2H-LEU-C1.gb")
            mapprefix = internal_no + "pYN2H-LEU-C1 GW.gb"
        elif plasmidtype == 12:
            nterm = ""
            templatefile = os.path.join(script_dir, "template-pYN2H-TRP-N2.gb")
            mapprefix = internal_no + "pYN2H-TRP-N2 GW.gb"
        elif plasmidtype == 13:
            nterm = ""
            templatefile = os.path.join(script_dir, "template-pYN2H-TRP-C2.gb")
            mapprefix = internal_no + "pYN2H-TRP-C2 GW.gb"
            
        # Get sequence data of CCSB clone from humanORFeome 7.1
        input_line = row[0]
        if "CCSB" in input_line:
            ccsbID = row[0].strip('CCSB_')
            if bank == 1: 
                url = 'http://horfdb.dfci.harvard.edu/index.php?page=showdetail&orf=' + ccsbID
            elif bank == 2:
                url = 'http://horfdb.dfci.harvard.edu/hv7/index.php?page=getresults&by=detail&qury=' + ccsbID
            response = requests.get(url)
            htmldata = response.text
            soup = BeautifulSoup(htmldata, features="html.parser")          
            orf = ""
            symbol = ""
            try:
                orf = soup.findAll('font', {"class": "seq"})[0].text
                #symbol = soup.findAll('table', {"class": "dtdata"})[0].text.strip('Symbol: ').strip()
                symbol = soup.findAll('table', {"class": "dtdata"})[0]
                if bank == 1:
                    symbol = re.search('Symbol:(.*)Resource', symbol.text).group(1).split()[0]
                elif bank == 2:
                    symbol = re.search('Symbol:(.*)Description', symbol.text).group(1).strip()
                #symbol = soup.findAll('td', {"colspan": "2"})
                #for r in symbol:
                #print r
            except IndexError:
                pass
        
            if len(orf) < 1:
                orf = input("Clone not found in human ORFeome database. Please enter ORF: ")
            if len(symbol) < 1:
                symbol = input("Please type in Gene Symbol of ORF: ")
        
            #remove stop-codon
            orf = orf[:-3]
        
        else:
            input_line = input_line.split()
            symbol = input_line[0]
            orf = input_line[1]
            orf = re.sub(r"\W", "", orf)
            ccsbID = "none"
        
        seq = nterm + orf
        seq_spaced = ' '.join(seq[i:i+10] for i in range(0, len(seq), 10))
        result = [seq_spaced[i:i+66] for i in range(0, len(seq_spaced), 66)]
        
        # Create row numbering (max. vector map size 15000)
        rownumbering = list(range(1,15000,60))
        
        # Create new file
        filename = mapprefix.replace('GW', symbol + "_CCSB_" + ccsbID)
        #filename = "CCSB_" + ccsbID + " " + mapprefix + ".gb"
        newfile = os.path.join(script_dir, filename)
        fnew = open(newfile, "w+")    
        
        # Read contents of template file and modify
        insert_start = str(len(seq)-len(orf)+1)
        insert_end = str(len(seq))
        
        f = open(templatefile, "r")
        filedata = f.read()
        filedata = filedata.replace('title', filename)
        filedata = filedata.replace('size', str(len(seq))) 
        filedata = filedata.replace('date', today)
        filedata = filedata.replace('insert_name', symbol + "_(CCSB_" + ccsbID + ")")
        filedata = filedata.replace('insert_start', insert_start)
        filedata = filedata.replace('insert_end', insert_end)   
        f.close()
        
        # Write modified template to new file
        fnew.write(filedata)
              
        # Create formatted seq with rownumbering and bases           
        i = 0
        combined_seq = list()
        while i < len(result):
            combined_seq.append(str(rownumbering[i]) + ' ' + result[i] + '\n')
            i += 1
        
        # Append formatted seq to new file and close
        fnew.seek(0,2)
        fnew.writelines(combined_seq)
        fnew.write('//')
        fnew.close()
